var elementStatus = [Things.SvgDataCollectorThing.notWorkingColor, Things.SvgDataCollectorThing.partlyWorkingColor, Things.SvgDataCollectorThing.workingColor];
var overrides = DataShapes["SvgOverrideDatashape"].CreateValues();
overrides.AddRow({
    elementName: "injecteur",
    "stroke": me.Injecteur1_Command ? Things.SvgDataCollectorThing.notWorkingColor : Things.SvgDataCollectorThing.workingColor,
    selectable: false
});
overrides.AddRow({
    elementName: "Injecteur1Text",
    "text": "Injecteur1: " + (me.Injecteur1_Command ? "ON" : "OFF"),
    "fill":  me.Injecteur1_Command ? Things.SvgDataCollectorThing.workingColor : Things.SvgDataCollectorThing.notWorkingColor,
    selectable: false
});
overrides.AddRow({
    elementName: "Injecteur2Text",
    "text": "Injecteur2: " + (me.Injecteur2_Command ? "ON" : "OFF"),
    "fill":  me.Injecteur2_Command ? Things.SvgDataCollectorThing.workingColor : Things.SvgDataCollectorThing.notWorkingColor,
    selectable: false
});
overrides.AddRow({
    elementName: "InjecteurMap",
    "fill": Things.SvgDataCollectorThing.selectableColor
});

var result = overrides;

